parameters.jsCode:
const output = $json.output;
const updateFields = [];
const dbName = $('Metadata dbName').first().json.postgresDbName;

updateFields.push(`"theme" = '${output.theme.replace(/'/g, "''")}'`);
updateFields.push(`"topics" = '${output.topics.replace(/'/g, "''")}'`);

// Ajouter painPoints seulement s'il existe et n'est pas undefined
if (output.painPoints && output.painPoints !== 'undefined') {
  updateFields.push(`"painPoints" = '${output.painPoints.replace(/'/g, "''")}'`);
}

updateFields.push(`"keywords" = '${output.keywords.replace(/'/g, "''")}'`);

const query = `
UPDATE ${dbName}
SET ${updateFields.join(', ')}
WHERE id = '${$('Loop Over Items').item.json.id}';`;

return { query };

