const maxStep = $('Set Current Step').first().json.currentStep;
const queryString = $('JobType Router').first().json.data.query;
const titleMatch = queryString.match(/Titre:\s*(.*?)\s*-\s*Auteur:/i);
const title = titleMatch ? titleMatch[1].trim() : "Titre inconnu";
const authorMatch = queryString.match(/Auteur:\s*(.*?)(?:\.\s*Etape:|\s*$)/i);
const author = authorMatch ? authorMatch[1].trim() : "Auteur inconnu";

return {
  pageId: $('JobType Router').first().json.pageId,
  jobType: "report",
  data: {
    query: `${title} - ${author}`,
    summaries: Array(maxStep)
      .fill(0)
      .flatMap((_, i) => {
        try {
          const results = $('Generate Summaries').all(0, i);
          if (results && results.length > 0) {
            return [{
              step: i + 1,
              summary: results.map(item => ({
                modelName: item.json.output.modelName,
                content: item.json.output.content
              }))
            }];
          }
          return [];
        } catch (e) {
          return [];
        }
      })
  }
};

