function formatSummaryWithColumns(html) {
  // 1. D'abord, corriger le HTML mal form√© o√π "id=" est directement coll√© √† "</div>"
  let correctedHtml = html.replace(/(<\/div>)\s*id="([^"]+)">(.*?<\/h[1-6]>)/g, '$1\n<h2 id="$2">$3');

  // 2. Trouver la section "1. Informations g√©n√©rales" sans capturer les sections suivantes
  const section1Pattern = /<h1[^>]*>1\.\s*Informations g√©n√©rales[^>]*>([\s\S]*?)(?=<h1|$)/i;
  const section1Match = correctedHtml.match(section1Pattern);

  if (section1Match) {
    // 3. Cr√©er la nouvelle mise en page √† deux colonnes pour cette section
    const imageUrl = $('Map imageUrl').first().json.imageUrl
    const originalContent = section1Match[0].replace(/<h1[^>]*>1\.\s*Informations g√©n√©rales[^>]*>/i, '');
    const newSection1 = `<h1 id="1informationsgnrales">1. Informations g√©n√©rales</h1>
    <div>
      <div>
        <img src="${imageUrl}">
      </div>
      <div>
        ${originalContent}
      </div>
    </div>`;

    // 4. Remplacer uniquement cette section, tout en pr√©servant le reste du document
    correctedHtml = correctedHtml.replace(section1Pattern, newSection1);
  }

  return correctedHtml;
}

