={{
(function(){
  // Nettoyage complet du texte JSON
  const cleanedJson = $json.text
    .replace(/```json/g, '')
    .replace(/```/g, '')
    .replace(/\n\s*/g, ' ')
    .trim();

  // Parse le JSON nettoy√©
  let blocks = cleanedJson.parseJson();
  blocks = Array.isArray(blocks) ? blocks : [blocks];

  // Filtrer tout bloc vide potentiel ou paragraphe contenant uniquement des espaces
  blocks = blocks.filter(block => {
    if (block.type === 'paragraph' && 
        block.paragraph.rich_text.length === 1 && 
        block.paragraph.rich_text[0].text.content.trim() === '') {
      return false;
    }
    return true;
  });

  return blocks;
})()
}}

