const length = $('JobType Router').first().json.data.transcripts.length;
const theme = $('JobType Router').first().json.data.theme;
const chapter = $('JobType Router').first().json.data.chapter;

return {
  pageId: $('JobType Router').first().json.pageId,
  jobType: "report",
  data: {
    theme: `${theme}`,
    chapter: `${chapter}`,
    summaries: Array(length)
      .fill(0)
      .flatMap((_, i) => {
        try {
          const results = $('Generate Summaries').all(0, i);
          if (results && results.length > 0) {
            return [{
              step: i + 1,
              summary: results.map(item => {
                // Parse the output string to get the JSON object
                const parsedOutput = JSON.parse(item.json.output);
                return {
                  modelName: parsedOutput.modelName,
                  content: parsedOutput.content
                };
              })
            }];
          }
          return [];
        } catch (e) {
          return [];
        }
      })
  }
};

