const urls = Object.values($('JobType Router').first().json.data.all_urls
  .reduce((acc, url) => ({ ...acc, [url]: url }),{}));
const chunksize = 50;
const splits = Math.max(1, Math.floor(urls.length/chunksize));

const blocks = Array(splits).fill(0)
  .map((_, idx) => {
    const block = urls
      .slice(
        idx * chunksize, 
        (idx * chunksize) + chunksize - 1
      )
      .map(url => {
        return {
          object: "block",
          type: "bulleted_list_item",
          bulleted_list_item: {
            rich_text: [
              { type: "text", text: { content: url } }
            ]
          }
        }
      });
    return { json: { block } }
  });

return [
  { json: {
    block:[{
      "object": "block",
      "type": "heading_2",
      "heading_2": {
        "rich_text": [
          {
            "type": "text",
            "text": {
              "content": "Sources"
            }
          }
        ]
      }
    }]
  } },
  ...blocks
];

