const entretien = $input.first().json;
const entreprise = entretien['Entreprise'];

return {
  title: `Entretien: ${entreprise}`,
  pageId: entretien.id,
  entreprise: entreprise,
  contrat: entretien.Contrat,
  typeEntretien: entretien['Type Entretien'],
  dateEntretien: entretien['Date Entretien'].start
};

const tasks = $input.first().json.tasks;
const targetTitle = $('Mapping').first().json.title;

const foundTask = tasks.find(task => task.title === targetTitle);

return foundTask ? {id: foundTask.id} : {id: null};

// D√©claration des constantes
const mapping = $('Mapping').first().json;
const agent = $('Agent Checklist').first().json;
const TRAVAIL_PROJECT_ID = '66ba4bbedb643ca0adb34ab0';

// Parse le JSON de l'agent (peut avoir des backticks markdown)
let checklistRaw = agent.text;
checklistRaw = checklistRaw.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
const checklistArray = JSON.parse(checklistRaw);

// Grouper par section et formater pour TickTick items
const sections = {};
checklistArray.forEach(item => {
  if (!sections[item.section]) {
    sections[item.section] = [];
  }
  sections[item.section].push(item.title);
});

// Construire le content avec sections
let content = `üìã Pr√©paration Entretien ${mapping.entreprise}\n\n`;
Object.keys(sections).forEach(section => {
  content += `${section}\n`;
  sections[section].forEach(item => {
    content += `  ‚Ä¢ ${item}\n`;
  });
  content += '\n';
});

// Construire les items pour TickTick (format subtasks)
const items = checklistArray.map(item => ({
  title: `${item.section} - ${item.title}`,
  status: 0
}));

return {
  projectId: TRAVAIL_PROJECT_ID,
  taskId: $('Extract Task If Found').first().json.id,
  title: mapping.title,
  content: content.trim(),
  startDate: mapping.dateEntretien,
  tags: ["ü§ñAuto", "üïí1h"],
  items: items
};

const blocks = $input.all();
let content = '';

for (const item of blocks) {
  const block = item.json;

  if (!block.type) continue;

  const blockData = block[block.type];

  if (blockData?.text && Array.isArray(blockData.text)) {
    const text = blockData.text
      .map(t => t.plain_text || '')
      .join('');

    if (!text.trim()) continue;

    switch(block.type) {
      case 'heading_1':
        content += `\n# ${text}\n`;
        break;
      case 'heading_2':
        content += `\n## ${text}\n`;
        break;
      case 'heading_3':
        content += `\n### ${text}\n`;
        break;
      case 'bulleted_list_item':
        content += `- ${text}\n`;
        break;
      case 'to_do':
        const checked = blockData.checked ? '[x]' : '[ ]';
        content += `- ${checked} ${text}\n`;
        break;
      default:
        content += `${text}\n`;
    }
  } else if (block.type === 'divider') {
    content += `\n---\n`;
  }
}

return [{json: {content: content.trim()}}];

